# 이동3-2


import math


MAX_K = math.ceil(math.log(1e9, 3))


K_POW = [1]
for i in range(1, MAX_K):
    K_POW.append(3*K_POW[i-1])


def solve(x: int, y: int, k=0) -> bool:

    if k >= MAX_K:
        return False
    if x == 0 and y == 0:
        return True
    coords = [
        (x-K_POW[k], y),
        (x, y-K_POW[k]),
        (x+K_POW[k], y),
        (x, y+K_POW[k]),
    ]
    for x, y in coords:
        if k+1 < MAX_K and not (0 < abs(x) < K_POW[k+1] and 0 < abs(y) < K_POW[k+1]):
            if solve(x, y, k+1):
                return True
    return False


if __name__ == "__main__":
    X, Y = map(int, input().split())
    print('1' if solve(X, Y) else '0')
